package leetcode.daily;

import java.util.*;

public class FindAnagrams {


    public static List<Integer> findAnagrams3(String s, String p) {
        List<Integer> ans = new ArrayList();

        if (s == null || s.length() < 1 || p == null || p.length() < 1 || p.length() > s.length()) {
            return ans;
        }

        if (s.equals(p)) {
            ans.add(0);
            return ans;
        }

        //  辅助数组
        int[] pdp = new int[26];
        int[] sdp = new int[26];

        char[] pChars = p.toCharArray();
        //  填充辅助辅助
        for (int i = 0; i < pChars.length; i++) {
            pdp[pChars[i] - 'a']++;
        }

        char[] sChars = s.toCharArray();

        //  双指针从头开始走,每次R走,L的变化由窗口大小决定
        for (int L = 0, R = 0; R < sChars.length; R++) {
            sdp[sChars[R] - 'a']++;

            //  判断两个辅助数组内词频是否相同
            ans.add(L);
            for (int i = 0; i < 26; i++) {
                if (sdp[i] != pdp[i]) {
                    ans.remove(Integer.valueOf(L));
                    break;
                }
            }

            //  什么时候触发
            if (R - L >= p.length() - 1) {
                //  超过窗口大小, L位置减一,并且L位置向R位置移动
                sdp[sChars[L++] - 'a']--;
            }
        }


        return ans;
    }


    public static List<Integer> findAnagrams2(String s, String p) {
        List<Integer> ans = new ArrayList();

        if (s == null || s.length() < 1 || p == null || p.length() < 1 || p.length() > s.length()) {
            return ans;
        }

        if (s.equals(p)) {
            ans.add(0);
            return ans;
        }

        char[] sourceChars = s.toCharArray();
        char[] targetChars = p.toCharArray();
        int sourceLength = sourceChars.length;
        int targetLength = targetChars.length;

        Map<Character, Integer> matchMap = new HashMap();
        if (sourceLength == targetLength) {
            for (char sourceChar : sourceChars) {
                matchMap.put(sourceChar, matchMap.getOrDefault(sourceChar, 0) + 1);
            }

            for (char targetChar : targetChars) {
                if (!matchMap.containsKey(targetChar)) {
                    return ans;
                }
                Integer indexSize = matchMap.get(targetChar);
                if (--indexSize < 0) {
                    return ans;
                }
                matchMap.put(targetChar, indexSize);
            }
            ans.add(0);
            return ans;
        } else {
            //  暴力遍历
            for (int i = 0; i < sourceLength - targetLength + 1; i++) {
                matchMap = new HashMap(targetLength);
                for (int L = i; L < i + targetLength; L++) {
                    matchMap.put(sourceChars[L], matchMap.getOrDefault(sourceChars[L], 0) + 1);
                }
                ans.add(i);
                for (char targetChar : targetChars) {
                    if (!matchMap.containsKey(targetChar)) {
                        ans.remove(Integer.valueOf(i));
                        break;
                    }
                    Integer indexSize = matchMap.get(targetChar);
                    if (--indexSize < 0) {
                        ans.remove(Integer.valueOf(i));
                        break;
                    }
                    matchMap.put(targetChar, indexSize);
                }
            }
        }
        return ans;
    }


    public static List<Integer> findAnagrams(String s, String p) {
        List<Integer> ans = new ArrayList();
        if (s == null || s.length() < 1 || p == null || p.length() < 1) {
            return ans;
        }
        if (p.length() > s.length()) {
            return ans;
        }
        if (s.equals(p)) {
            ans.add(0);
            return ans;
        }

        char[] sourceChars = s.toCharArray();
        char[] targetChars = p.toCharArray();
        int targetLength = targetChars.length;

        Map<Character, Integer> matchMap = new HashMap();
        if (p.length() == s.length()) {
            for (char sourceChar : sourceChars) {
                matchMap.put(sourceChar, matchMap.getOrDefault(sourceChar, 0) + 1);
            }

            for (char targetChar : targetChars) {
                if (!matchMap.containsKey(targetChar)) {
                    return ans;
                }
                Integer indexSize = matchMap.get(targetChar);
                if (--indexSize < 0) {
                    return ans;
                }
                matchMap.put(targetChar, indexSize);
            }
            ans.add(0);
            return ans;
        } else {

            List<List<Character>> list = new ArrayList<>();
            for (int i = 0; i < sourceChars.length - targetLength + 1; i++) {
                List<Character> characterList = new ArrayList<>();
                for (int L = i; L < i + targetLength; L++) {
                    characterList.add(sourceChars[L]);
                }
                list.add(characterList);
            }

            for (int i = 0; i < list.size(); i++) {
                matchMap = new HashMap();

                if (list.get(i).size() < targetLength) {
                    continue;
                }
                ans.add(i);
                for (Character character : list.get(i)) {
                    matchMap.put(character, matchMap.getOrDefault(character, 0) + 1);
                }

                for (char targetChar : targetChars) {
                    if (!matchMap.containsKey(targetChar)) {
                        ans.remove(Integer.valueOf(i));
                        break;
                    }
                    Integer indexSize = matchMap.get(targetChar);
                    if (--indexSize < 0) {
                        ans.remove(Integer.valueOf(i));
                        break;
                    }
                    matchMap.put(targetChar, indexSize);
                }
            }
        }
        return ans;
    }

    public static void main(String[] args) {

        String s = "";
        String p = "";

        long starTime = System.currentTimeMillis();
        List<Integer> anagrams1 = findAnagrams3(s, p);
        System.out.println(anagrams1);
        System.out.println(System.currentTimeMillis() - starTime);

        starTime = System.currentTimeMillis();
        List<Integer> anagrams2 = findAnagrams2(s, p);
        System.out.println(anagrams2.toString());
        System.out.println(System.currentTimeMillis() - starTime);
    }
}
